import express from 'express';
import { ApplicationState } from './state';

const app = express();
const port = process.env.PORT || 8080;

const state = new ApplicationState();

app.get('/health', (req, res) => {
  res.status(200).json({ status: 'healthy' });
});

app.get('/ready', (req, res) => {
  res.status(200).json({ status: 'ready' });
});

app.get('/state', (req, res) => {
  res.status(200).json({
    appName: state.getAppName(),
    totalSessions: state.getTotalSessions()
  });
});

app.listen(port, () => {
  console.log(`ApplicationStateService is running on port ${port}`);
});

export class ApplicationState {
  private appName: string;
  private totalSessions: number;

  constructor() {
    this.appName = 'Legacy Customer Orders';
    this.totalSessions = 0;
  }

  public getAppName(): string {
    return this.appName;
  }

  public getTotalSessions(): number {
    return this.totalSessions;
  }

  public incrementSessions(): void {
    this.totalSessions += 1;
  }

  public decrementSessions(): void {
    this.totalSessions -= 1;
  }
}

import { ApplicationState } from './state';

describe('ApplicationState', () => {
  let state: ApplicationState;

  beforeEach(() => {
    state = new ApplicationState();
  });

  test('should initialize with default values', () => {
    expect(state.getAppName()).toBe('Legacy Customer Orders');
    expect(state.getTotalSessions()).toBe(0);
  });

  test('should increment total sessions', () => {
    state.incrementSessions();
    expect(state.getTotalSessions()).toBe(1);
  });

  test('should decrement total sessions', () => {
    state.incrementSessions();
    state.decrementSessions();
    expect(state.getTotalSessions()).toBe(0);
  });
});

